import React, { useMemo } from "react";
import { css } from "emotion";
import PropTypes from "prop-types";
import drone from "../icons/drone.svg";
import tractor from "../icons/tractor.svg";

const propTypes = {
  cluster: PropTypes.object,
  id: PropTypes.string,
  setIdOfToolTip: PropTypes.func,
};

const getBackgroundColor = (status) => {
  if (status === "on-mission") {
    return "green";
  } else if (status === "unreachable") {
    return "red";
  } else if (status === "idle") {
    return "orange";
  } else return "red";
};

const VehicleComponent = ({
  cluster: { id, type, status, location },
  setIdOfToolTip,
  idOfToolTip,
}) => {
  const isTractorImg = useMemo(() => type === "tractor", [type]);

  return (
    <div
      className={styles.root}
      onMouseEnter={() => setIdOfToolTip(id)}
      onMouseLeave={() => setIdOfToolTip(null)}
    >
      <div className={styles.status({ status })} />
      {idOfToolTip === id && <div className={styles.toolTip}> hey </div>}
      <img
        src={isTractorImg ? tractor : drone}
        alt="car"
        width={30}
        height={30}
      />
    </div>
  );
};

const styles = {
  root: css({
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    position: "relative",
  }),
  toolTip: css({
    position: "absolute",
    top: -50,
    borderRadius: 8,
    zIndex: 1,
    background: "blue",
    color: "white",
    padding: 12,
  }),
  status: ({ status }) =>
    css({
      background: getBackgroundColor(status),
      height: 12,
      width: 12,
      borderRadius: "50%",
    }),
};

VehicleComponent.propTypes = propTypes;

export default VehicleComponent;
