import React, {useMemo } from 'react';
import { css } from 'emotion';
import PropTypes from 'prop-types';
import drone from '../icons/drone.svg';
import tractor from '../icons/tractor.svg'

const propTypes = {
  type: PropTypes.string,
};

const VehicleComponent = ({ type }) => {

  const isTractorImg = useMemo(
    () => type === 'tractor'
  ,[type]);

  return (
    <div className={styles.root}>
      <img src={isTractorImg ? tractor : drone} alt='car' width={30} height={30}/>
    </div>
  );
};

const styles = {
  root: css({
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
  }),
  text: css({
    fontSize: 10,
  })
};

VehicleComponent.propTypes = propTypes;

export default VehicleComponent;
